<script>

export default {

  data () {
    return {
      teamIndex: 0,
      team: {
        0: {
          name: this.$i18n.t('OurTeamNameOne'),
          prof: this.$i18n.t('OurTeamProfOne')
        },
        1: {
          name: this.$i18n.t('OurTeamNameThree'),
          prof: this.$i18n.t('OurTeamProfThree')
        },
        2: {
          name: this.$i18n.t('OurTeamNameTwo'),
          prof: this.$i18n.t('OurTeamProfTwo')
        }
      },
      phoneSliderOptions: {
        slidesPerView: 3,
        loop: true,
        centeredSlides: true,
        navigation: {
          nextEl: '.works__slider-button-next',
          prevEl: '.works__slider-button-prev'
        }
      },
      testimonialsSliderOptions: {
        pagination: {
          el: '.swiper-pagination',
          clickable: true
        },
        navigation: {
          nextEl: '.testimonials__slider-button-next',
          prevEl: '.testimonials__slider-button-prev'
        },
        breakpoints: {
          320: {
            slidesPerView: 1,
            slidesPerGroup: 1,
            spaceBetween: 0
          },
          800: {
            slidesPerView: 2,
            slidesPerGroup: 2,
            spaceBetween: 30
          },
          900: {
            slidesPerView: 3,
            slidesPerGroup: 3,
            spaceBetween: 30
          }
        }
      },
      teamSliderOptions: {
        slidesPerView: 1,
        loop: true,
        navigation: {
          nextEl: '.team__slider-button-next',
          prevEl: '.team__slider-button-prev'
        },
        breakpoints: {
          800: {
            slidesPerView: 2,
            spaceBetween: 30
          },
          1250: {
            slidesPerView: 3,
            spaceBetween: 30
          }
        }
      },
      scrollYLast: 0,
      top: 0,
      botom: false
    }
  },

  beforeMount  () {
    window.addEventListener('scroll', this.handleScroll)
  },
  beforeDestroy () {
    window.removeEventListener('scroll', this.handleScroll)
  },
  methods: {
    handleScroll () {
      // if (this.$refs.robot.getBoundingClientRect().bottom > 350 && this.$refs.robot.getBoundingClientRect().bottom < 450) {
      // }
      // this.$refs.robot.setAttribute('opacity', '0')
      console.log(window.scrollY)

      if (window.scrollY > this.scrollYLast) {
        if (this.$refs.robot.getBoundingClientRect().bottom < 150 && this.top === 0) {
          this.$refs.robot.style.transform = 'translate(0, 0)'
          this.top = 1
          this.botom = true
        }

        if (this.$refs.robot.getBoundingClientRect().bottom < 150 && this.top === 1) {
          this.$refs.robot.setAttribute('opacity', '0')

          setTimeout(() => {
            this.$refs.robot.style.transform = 'translate(1413px, 670px)'
            this.$refs.robot.setAttribute('opacity', '1')
            this.top = 2
          }, 500)
        }

        if (this.$refs.robot.getBoundingClientRect().bottom < 150 && this.top === 2) {
          this.$refs.robot.setAttribute('opacity', '0')

          setTimeout(() => {
            this.$refs.robot.style.transform = 'translate(0, 1320px)'
            this.$refs.robot.setAttribute('opacity', '1')
            this.top = 3
          }, 500)
        }

        if (this.$refs.robot.getBoundingClientRect().bottom < 150 && this.top === 3) {
          this.$refs.robot.setAttribute('opacity', '0')
          setTimeout(() => {
            this.$refs.robot.style.transform = 'translate(1413px, 1990px)'
            this.$refs.robot.setAttribute('opacity', '1')
          }, 500)
        }
      } else {
        if (this.$refs.robot.getBoundingClientRect().bottom > 800 && this.top === 3) {
          this.$refs.robot.setAttribute('opacity', '0')
          setTimeout(() => {
            this.$refs.robot.style.transform = 'translate(0, 1320px)'
            this.$refs.robot.setAttribute('opacity', '1')
            this.top = 2
          }, 500)
        }

        if (this.$refs.robot.getBoundingClientRect().bottom > 800 && this.top === 2) {
          this.$refs.robot.setAttribute('opacity', '0')
          setTimeout(() => {
            this.$refs.robot.style.transform = 'translate(1413px, 670px)'
            this.$refs.robot.setAttribute('opacity', '1')
            this.top = 1
          }, 500)
        }

        if (this.$refs.robot.getBoundingClientRect().bottom > 800 && this.top === 1) {
          this.$refs.robot.setAttribute('opacity', '0')
          setTimeout(() => {
            this.$refs.robot.style.transform = 'translate(1413px, 670px)'
            this.$refs.robot.setAttribute('opacity', '1')
            this.top = 0
          }, 500)
        }

        if (this.$refs.robot.getBoundingClientRect().bottom > 200 && this.top === 0) {
          if (this.botom) {
            this.$refs.robot.setAttribute('opacity', '0')
            this.botom = false
            setTimeout(() => {
              this.$refs.robot.style.transform = 'translate(0, 0)'
              this.$refs.robot.setAttribute('opacity', '1')
              this.top = 0
            }, 500)
          } else {
            this.$refs.robot.style.transform = 'translate(0, 0)'
          }
        }
      }
      this.scrollYLast = window.scrollY
    }
  }
}
</script>
